import React from 'react';
import { Container } from 'react-bootstrap';
import { useForm } from 'react-hook-form';
import Field from '../components/FieldForm';
import {post,get} from 'axios';
import { useMutation, useQuery } from 'react-query';
import { setToken, isAuthenticated } from '../functions/token';
import {fetchAll, fetchUser} from '../functions/api'
import { setUser } from '../functions/user';
import { Redirect } from 'react-router';

const LoginForm = () => {

   

    const loggedIn = isAuthenticated() ? <Redirect to="/" /> : '';
    const {control, handleSubmit}  = useForm();
    // const userData = useQuery('user', fetchUser, {enabled: false})
    // const mutationToken = useMutation(data => post('http://localhost:8000/api/rest-auth/login/',data));
    const set = (token,user) => {setToken(token); setUser(user)}
    const onSubmit = (data) => {
        fetchAll(data).then(({token,user}) => {set(token,user)} )
        // mutationToken.mutate(data);
        // if(mutationToken.isSuccess) {
        //     const token = mutationToken.data.data.token;
        //     setTimeout(() => {setToken(`Token ${token}`)},100)
        //     fetchUser().then(data => {setUser(JSON.stringify(data))});


            
    //         if(sessionStorage.getItem('token')) {
    //            setTimeout(() => {
    //                userData.refetch();
    //                if(userData.isSuccess) {
    //                    setTimeout(() => {setUser(JSON.stringify(userData.data))},300)
    //                }
    //            },500)

        
    // } }
} 
    
    return (
        <Container>
            <div>
                <Field control={control} label="username" placeholder="username" type="text" required name="username" />
                <Field control={control} label="password" type="password" required placeholder="*******" name="password"  />
                <div className="d-grid gap-2">
                    <button className="btn btn-secondary" onClick={handleSubmit(onSubmit)}>Login</button>
                </div>
            </div>
            {loggedIn}
        </Container>

      );
 }
 
export default LoginForm;
