import React from "react";
import BlogEditor from "../components/BlogEditor";
import { Container } from "react-bootstrap";
import { useForm } from "react-hook-form";
import { useMutation } from "react-query";
// import draftToHtml from "draftjs-to-html";
import Field from "../components/FieldForm";
import axios from 'axios';
import File from "../components/FileInput";
import { isAuthenticated } from "../functions/token";
import { Redirect } from "react-router";
import { getToken } from "../functions/token";
import { getUser } from "../functions/user";

const BlogAll = (props) => {
  const {  handleSubmit, control } = useForm();
  const user = JSON.parse(getUser());
  const mutation = useMutation(data => axios.post('http://localhost:8000/api/blogs/', data,{
      headers: {
        "content-type": "multipart/form-data",
        "Authorization": getToken(),
          
      }}));
  
  
      const isAuth = isAuthenticated();
      const RedirectToAuth = !isAuth ? (
        <Redirect to="/login"></Redirect>
      ) : (
        <div></div>
      );

  const onSubmit = (data) => {
    const image = data.image_new
    const newData = {...data, innerText: draftToHtml(data.innerText), username: user.username, created_by: `${user.first_name + user.last_name}` };
    const formData = new FormData();
    console.log(data)
    for (const [key, value] of Object.entries(newData)) {
        formData.append(key,value)
      }
      // formData.append("image_new", image[0])
      // console.log(formData.get('image_new'))
      axios.post('http://localhost:8000/api/blogs/', formData,{
      headers: {
        "content-type": "multipart/form-data",
        "Authorization": getToken(),
          
      }})
    //   mutation.mutate(formData)
    // mutation.data
    
    };

  return (
    <Container>
      {RedirectToAuth}

        <Container className="py-3">
          <Field
            type="text"
            label="Title"
            placeholder="Input the title of the blog"
            name="title"
            control={control}
          />
        
            <File control={control} name="image_new" label="Blog Image" />
          <BlogEditor control={control} />
          <div className="my-3">
        
              <div className="d-grid gap-2">
                  <button className="btn btn-info" onClick={handleSubmit(onSubmit)}>Submit Blog</button>
              </div>
          </div>
        </Container>
    </Container>
  );
};

export default BlogAll;
