import React, { useState } from "react";
import BlogCard from "../components/BlogCard";
import BlogAll from "../components/BlogForm";
import { useQuery } from "react-query";
import { fetchBlogs } from "../functions/api";
import Loader from "react-loader-spinner";
import { Container } from "react-bootstrap";
import { BooleanField } from "react-admin";
import { RedirectToAuth } from "../functions/token";
import { Redirect } from "react-router";
const BlogView = (props) => {
  
  const [writing, setWriting] = useState(false);
  const { data, isSuccess } = useQuery("blogs", fetchBlogs);
  const blogsCards = ({ blogsData }) => {
    return blogsData.map((blog) => (
      <div className="d-flex justify-content-center my-3">
        <BlogCard
          title={blog.title}
          text={blog.innerText}
          id={blog.id}
          img={blog.image_new}
          created_by={blog.created_by}
        ></BlogCard>
      </div>
    ));
  };
  const loaderWrapper = (
    <div className="d-flex justify-content-center my-5">
      <Loader type="Grid" color="#00BFFF" height={300} width={300} />
    </div>
  );
  const blogs = isSuccess ? blogsCards(data) : loaderWrapper;
  const write = writing ? <BlogAll /> : "";
  return (
    <Container className="my-3">
      {blogs}
      <div className="my-3">
        <div className="d-grid gap-2">
          <button
            onClick={() => {
              setWriting(true);
            }}
            className="btn btn-success"
          >
            Write !
          </button>
        </div>
        {write}
        <Redirect to="/login" />
      </div>
    </Container>
  );
};

export default BlogView;
