import React from 'react';
import BlogCard from '../components/BlogCard';
import BlogAll from '../components/BlogForm';
import { useQuery } from 'react-query';
import { fetchBlogs } from '../functions/api';
import Loader from "react-loader-spinner";
import { Container } from 'react-bootstrap';
import { BooleanField } from 'react-admin';

const BlogView = (props) => {
    const {data, isSuccess} = useQuery('blogs', fetchBlogs);
    const blogsCards = ({blogsData}) => {
        return blogsData.map(blog => (
            <div className="d-flex justify-content-center my-3">
                <BlogCard title={blog.title} text={blog.innerText} id={blog.id} img={blog.image_new} created_by={blog.created_by} ></BlogCard>
            </div>
        ))
    }
    const blogs = isSuccess ? blogsCards(data): <Loader type="Grid" color="#00BFFF" height={300} width={300} />
    
    return ( 
        <Container className="my-3">
            {blogs}
        </Container>
     );
}
 
export default BlogView;
