import React, { useState } from "react";
import { Card, Row, Col } from "react-bootstrap";
import {Link} from 'react-router-dom'
const BlogCard = (props) => {
  const [color, setColor] = useState("black");
  return (
    <Card
      body
      style={{ width: "40rem" }}
      border={color}
      onMouseEnter={() => {
        setColor("info");
      }}
      onMouseLeave={() => {
        setColor("black");
      }}
    >
      <Row>
        <Col md={5} xl={5}>
          <img
            style={{maxWidth: '100%'}}
            src="https://www.experiencecurve.com/wp-content/uploads/2020/05/43617178595_0c8ac1b22b_b.jpg"
            alt=""
          />
        </Col>
        <Col md={7} xl={7}>
        <Link to="/2"><strong>Social Media Effects</strong></Link>
        </Col>
      </Row>
    </Card>
  );
};

export default BlogCard;
