import React from 'react';
import { Container } from 'react-bootstrap';
import { useForm } from 'react-hook-form';
import Field from '../components/FieldForm';
import axios, {post} from 'axios';
import { useMutation } from 'react-query';
const LoginForm = () => {
    const {control, handleSubmit}  = useForm();
    const mutationToken = useMutation(data => axios.post('https://localhost:8000/api/rest-auth/login/',data));
    const mutationUser = useMutation(data => axios.post('https://localhost:8000/api/rest-auth/user/',data));

    
    return (
        <Container>
            <div>
                <Field control={control} label="username" placeholder="username" type="text" requires />
                <Field control={control} label="password" type="password" required placeholder="*******"  />
            </div>
        </Container>
      );
}
 
export default LoginForm;
