import React from 'react';
import { Container } from 'react-bootstrap';
import { useForm } from 'react-hook-form';
import Field from '../components/FieldForm';
import axios, {post} from 'axios';
import { useMutation, useQuery } from 'react-query';
import { setToken } from '../functions/token';
import { fetchUser } from '../functions/api';
const LoginForm = () => {
    const {control, handleSubmit}  = useForm();

    const mutationToken = useMutation(data => axios.post('http://localhost:8000/api/rest-auth/login/',data));
    const onSubmit = (data) => {
        mutationToken.mutate(data);
        if(mutationToken.isSuccess) {
            const token = mutationToken.data.data.key
            setToken(`Token ${token}`)
            fetchUser().then(data => {console.log(data)})
           
        }
    }
    
    return (
        <Container>
            <div>
                <Field control={control} label="username" placeholder="username" type="text" required name="username" />
                <Field control={control} label="password" type="password" required placeholder="*******" name="password"  />
                <div className="d-grid gap-2">
                    <button className="btn btn-secondary" onClick={handleSubmit(onSubmit)}>Login</button>
                </div>
            </div>
        </Container>
      );
}
 
export default LoginForm;
