import React from 'react';
import { useQuery } from 'react-query';
import {Container, Row,Col,} from 'react-bootstrap'
import { useParams } from 'react-router-dom'
import {get} from 'axios';
import { getToken } from '../functions/token';

const BlogPost = (props) => {
    const {id} = useParams();

    const fetchBlogPost = async () => {
        const res = await get(`http://localhost:8000/api/blogs/${id}`, {headers: {Authorization: getToken()}});
        const data = await res.data
        return data;
    
    }

    
    const {isSuccess, data} = useQuery(`blog-${id}`,fetchBlogPost);
    const blog = isSuccess ? (
        <div>
            <img src={data.image_new} alt="blog image" />
            <hr color="black" />
        </div>
    ) : '';
   
    return ( 
        <Container className="mt-4">
           
        {blog}
        </Container>
     );
}
 
export default BlogPost;
