import React, { useState } from "react";
import { Navbar, Container, Nav } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import { useLocation } from "react-router";
import { isAuthenticated } from "../functions/token";
import { getUser } from "../functions/user";
const NavbarBlog = () => {
  const isAuth = isAuthenticated();
    const user = JSON.parse(getUser())
    console.log(user)
    const out = () => {
        sessionStorage.removeItem('token')
        sessionStorage.removeItem('user')
    }
    const authStyle = isAuth ?<LinkContainer to="/" activeStyle style={{color: 'black'}} ><Nav.Link active >Hi, {user.username} </Nav.Link></LinkContainer>:  <LinkContainer to="/login" style={{color: 'black'}}><Nav.Link>Login</Nav.Link></LinkContainer>
  return (
    <div>
        <Navbar bg="info" expand="md">
          <Container className="d-flex justify-content-center">
            <LinkContainer to="/">
              <Navbar.Brand>
                  <img src="https://www.freeiconspng.com/uploads/blogger-logo-icon-png-1.png" height={40} alt="Blog" />
              </Navbar.Brand>
            </LinkContainer>
          </Container>
        </Navbar>
        <Navbar bg="secondary" expand="md">
          <Container className="d-flex justify-content-center">
            <LinkContainer to="/create" style={{color: 'black'}} ><Nav.Link>Create</Nav.Link></LinkContainer>
            {authStyle}
            <LinkContainer to="/" style={{color: 'black'}}><Nav.Link>Home</Nav.Link></LinkContainer>
          </Container>
        <div className="me-auto">
        </div>
        </Navbar>
    </div>
  );
};

export default NavbarBlog;
