import React from "react";
import BlogEditor from "../components/BlogEditor";
import { Container } from "react-bootstrap";
import { useForm, Controller } from "react-hook-form";
import { useMutation } from "react-query";
import draftToHtml from "draftjs-to-html";
import Field from "../components/FieldForm";
import axios from 'axios';
import File from "../components/FileInput";
const BlogAll = (props) => {
  const { register, handleSubmit, control } = useForm();
  
  const mutation = useMutation(data => axios.post('http://localhost:8000/api/blogs', data));
  
  const onSubmit = (data) => {
    const newData = {...data, innerText: draftToHtml(data.innerText)};
    mutation.mutate(newData)
    };

  return (
    <Container className="py-3">
      <Field
        type="text"
        label="Title"
        placeholder="Input the title of the blog"
        name="title"
        control={control}
      />
      
        <File control={control} name="image_new" label="Blog Image" />

      <BlogEditor control={control} />
      <div className="my-3">
          <div className="d-grid gap-2">
              <button className="btn btn-info" onClick={handleSubmit(onSubmit)}>Submit Blog</button>
          </div>
      </div>
    </Container>
  );
};

export default BlogAll;
