import React, { useState } from "react";
import BlogCard from "../components/BlogCard";
import BlogAll from "../components/BlogForm";
import { useQuery } from "react-query";
import { fetchBlogs } from "../functions/api";
import Loader from "react-loader-spinner";
import { Alert, Container } from "react-bootstrap";
import { BooleanField } from "react-admin";
import { isAuthenticated } from "../functions/token";
import { Redirect } from "react-router";
import { Link } from "react-router-dom";

const BlogView = ({location}) => {
  const isAuth = isAuthenticated();
  const RedirectToAuth = !isAuth ? (
    <Redirect to="/login"></Redirect>
  ) : (
    <div></div>
  );
  const message = location.state.message !== 'undefined' ? <Alert variant="warning">{location.state.message}</Alert> : '';
  setTimeout(() => {location.state.message = ''},2000)
  const [writing, setWriting] = useState(false);
  const { data, isSuccess } = useQuery("blogs", fetchBlogs);
  const blogsCards = ({ blogsData }) => {
    return blogsData.map((blog) => (
      <div className="d-flex justify-content-center my-3" key={blog.id}>
        <BlogCard
          title={blog.title}
          text={blog.innerText}
          id={blog.id}
          img={blog.image_new}
          created_by={blog.created_by}
        ></BlogCard>
      </div>
    ));
  };
  const loaderWrapper = (
    <div className="d-flex justify-content-center my-5">
      <Loader type="Grid" color="#00BFFF" height={200} width={200} />
    </div>
  );
  const blogs = isSuccess ? blogsCards(data) : loaderWrapper;
  const button = isSuccess ? (
    <Link to="/create" className="btn btn-success">
      Write A Blog{" "}
    </Link>
  ) : (
    ""
  );
  return (
    <Container className="my-3">
      {RedirectToAuth}
      {message}
      {blogs}
      <div className="my-3">
        <div className="d-grid gap-2">{button}</div>
      </div>
    </Container>
  );
};

export default BlogView;
