import React from 'react';
import {get} from 'axios';
import { getToken } from './token';

export const fetchUser = async () => {
    const res = await get('http://localhost:8000/api/rest-auth/user/', {
        Headers: {
            Authorization: localStorage.getItem('token'),
        }
    });
    const data = res.data;

    return data
}
