import { Editor } from 'react-draft-wysiwyg';
import '../../node_modules/react-draft-wysiwyg/dist/react-draft-wysiwyg.css';
import { useForm, Controller } from 'react-hook-form';
import { useMutation } from 'react-query';
import axios from 'axios';
import { stateToHTML } from "draft-js-export-html";
import draftToHtml from 'draftjs-to-html';



const BlogEditor = (props) => {
    const [value, setValue] = useState('')
    const {register, data, handleSubmit, control} = useForm();
    const [editorS, setEditorS] = useState('')
    

    return ( 
        <div>
           <Controller
           name="innerText"
           control={control}
           render={({field}) => (<Editor 
            {...field}
        //    defaultEditorState={editorS}
        //    onChange={editor => setEditorS(draftToHtml(editor))}
        
           />)}
           />
            <button onClick={handleSubmit(data => {{console.log({...data, innerText: draftToHtml(data.innerText)})}})}>Click</button>
        </div>

     );
}
 
export default BlogEditor;
